$(document).ready(function(){$(".country_lists").click(function(){var c=$(this).attr("id");var a=c.split("_")[1];var b=$(".province_"+a).eq(1).css("display");$(".province_"+a).slideToggle("slow");if(b=="table-row"){$("#country_"+a).find("i").html("add")}else{$("#country_"+a).find("i").html("remove")}})});