function updateCountdown(a,b){let counterEl=b.find("span");let limit=a.data("limit");let remaining=limit-a.val().length;counterEl.text(remaining);counterEl.toggleClass("warning",remaining<0)}function makeCountable(a){let input=a.find(":input");let limit=input.data("limit");let comment=a.find(".help-block");comment.html(comment.html().replace(":limit",limit));updateCountdown(input,comment)}$(document).on("keyup change",".countable",function(){makeCountable($(this))});$(document).on("ajaxSuccess ready",function(){$(".countable").each(function(){makeCountable($(this))})});